import React, { useState } from 'react';
import { Box, Typography, IconButton, Card, CardContent } from '@mui/material';
import { Delete as DeleteIcon, Edit as EditIcon, RadioButtonUnchecked, RadioButtonChecked } from '@mui/icons-material';
import EditVitalTaskModal from './EditVitalTaskModal'; // Import the new modal

// Sample data matching the design
const initialVitalTasks = [
  { 
    id: 1, 
    title: "Walk the dog", 
    description: "Take the dog to the park and bring treats as well....", 
    fullDescription: "Take Luffy and Jiro for a leisurely stroll around the neighborhood. Enjoy the fresh air and give them the exercise and mental stimulation they need for a happy and healthy day. Don't forget to bring along squeaky and fluffy for some extra fun along the way!\n\n1. Listen to a podcast or audiobook\n2. Practice mindfulness or meditation\n3. Take photos of interesting sights along the way\n4. Practice obedience training with your dog\n5. Chat with neighbors or other dog walkers\n6. Listen to music or an upbeat playlist",
    priority: "Extreme", 
    status: "Not Started", 
    createdOn: "20/06/2023", 
    image: "https://images.unsplash.com/photo-1556228720-195a672e8a03?fit=crop&w=600&h=400"
  },
  { 
    id: 2, 
    title: "Go back home and take grandma to the hospital", 
    description: "Appointment with Dr. Smith at 2 PM.", 
    fullDescription: "Ensure grandma has her medical files and any required medication. The appointment is at the City General Hospital, Cardiology department. Plan to leave by 1 PM to account for traffic.",
    priority: "Moderate", 
    status: "In Progress", 
    createdOn: "20/06/2023", 
    image: "https://images.unsplash.com/photo-1584982751601-97dcc096659c?fit=crop&w=600&h=400" 
  },
];

// Helper to get priority color
const getPriorityColor = (priority) => {
    if (priority === 'Extreme') return '#ff5757';
    if (priority === 'Moderate') return '#3ABEFE';
    return '#4caf50'; // Low
};

function VitalTask() {
  const [tasks, setTasks] = useState(initialVitalTasks);
  const [selectedTask, setSelectedTask] = useState(tasks[0]);
  const [isEditModalOpen, setEditModalOpen] = useState(false);

  const handleDeleteTask = (taskId) => {
    if (window.confirm("Are you sure you want to delete this vital task?")) {
      const newTasks = tasks.filter(task => task.id !== taskId);
      setTasks(newTasks);
      // If the deleted task was the selected one, select the first task or null
      if (selectedTask && selectedTask.id === taskId) {
        setSelectedTask(newTasks.length > 0 ? newTasks[0] : null);
      }
    }
  };

  const handleUpdateTask = (updatedTask) => {
    const newTasks = tasks.map(task => (task.id === updatedTask.id ? updatedTask : task));
    setTasks(newTasks);
    setSelectedTask(updatedTask); // Also update the selected task view
    setEditModalOpen(false);
  };

  return (
    <>
      <EditVitalTaskModal 
        open={isEditModalOpen} 
        onClose={() => setEditModalOpen(false)} 
        taskToEdit={selectedTask}
        onUpdateTask={handleUpdateTask}
      />
      <Box className="vital-task-page">
        {/* Left Column: Task List */}
        <Box className="vital-tasks-list">
          {tasks.map(task => (
            <Card 
              key={task.id} 
              className={`vital-task-card ${selectedTask?.id === task.id ? 'selected' : ''}`}
              onClick={() => setSelectedTask(task)}
            >
              <CardContent className="vital-task-card-content">
                <Box className="vital-task-header">
                    {task.status === 'In Progress' ? <RadioButtonChecked sx={{ color: getPriorityColor(task.priority) }} /> : <RadioButtonUnchecked />}
                    <Typography variant="h6" className="vital-task-title">{task.title}</Typography>
                </Box>
                <Typography variant="body2" className="vital-task-desc">{task.description}</Typography>
                <img src={task.image} alt={task.title} className="vital-task-img-small" />
                <Box className="vital-task-footer">
                  <Typography>Priority: <span style={{ color: getPriorityColor(task.priority) }}>{task.priority}</span></Typography>
                  <Typography>Status: <span style={{ color: getPriorityColor(task.priority) }}>{task.status}</span></Typography>
                </Box>
              </CardContent>
            </Card>
          ))}
        </Box>

        {/* Right Column: Task Details */}
        <Box className="vital-task-details">
          {selectedTask ? (
            <>
              <img src={selectedTask.image} alt={selectedTask.title} className="vital-detail-img-large" />
              <Box className="vital-detail-meta">
                  <Typography>Priority: <span style={{ color: getPriorityColor(selectedTask.priority) }}>{selectedTask.priority}</span></Typography>
                  <Typography>Status: <span style={{ color: getPriorityColor(selectedTask.priority) }}>{selectedTask.status}</span></Typography>
                  <Typography>Created on: {selectedTask.createdOn}</Typography>
              </Box>
              <Typography variant="h4" className="vital-detail-title">{selectedTask.title}</Typography>
              <Typography variant="body1" className="vital-detail-desc" style={{ whiteSpace: 'pre-wrap' }}>
                {selectedTask.fullDescription}
              </Typography>
              <Box className="detail-actions">
                <IconButton className="delete-btn" onClick={() => handleDeleteTask(selectedTask.id)}>
                  <DeleteIcon />
                </IconButton>
                <IconButton className="edit-btn" onClick={() => setEditModalOpen(true)}>
                  <EditIcon />
                </IconButton>
              </Box>
            </>
          ) : (
            <Typography sx={{color: 'white', textAlign: 'center', mt: 5}}>No vital tasks. Add one to get started!</Typography>
          )}
        </Box>
      </Box>
    </>
  );
}

export default VitalTask;
