import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Box, TextField, Button, Typography, Paper, Link as MuiLink, CircularProgress } from '@mui/material';
import { toast } from 'react-toastify';

function ForgotPassword() {
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/forgot-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.message || 'Failed to send reset link');
      }
      toast.success(data.message);
    } catch (err) {
      toast.error(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#f9fafb',
      }}
    >
      <Paper component="div" elevation={3} sx={{ p: 4, borderRadius: 3, maxWidth: '400px', width: '100%' }}>
        <Box sx={{ textAlign: 'center', mb: 3 }}>
          <Typography variant="h4" component="h1" sx={{ fontWeight: 'bold' }}>Forgot Password</Typography>
          <Typography sx={{ color: 'text.secondary', mt: 1 }}>No problem! Enter your email below.</Typography>
        </Box>
        <Box component="form" onSubmit={handleSubmit}>
          <Typography variant="subtitle2" sx={{ fontWeight: 600, mb: 1 }}>Email</Typography>
          <TextField
            type="email"
            variant="outlined"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            fullWidth
            sx={{ mb: 3 }}
          />
          <Button
            type="submit"
            variant="contained"
            color="primary"
            size="large"
            fullWidth
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} color="inherit" /> : 'Send Reset Link'}
          </Button>
        </Box>
        <Typography sx={{ mt: 3, textAlign: 'center' }}>
          <MuiLink component={Link} to="/login" sx={{ fontWeight: 'bold' }}>Back to Login</MuiLink>
        </Typography>
      </Paper>
    </Box>
  );
}

export default ForgotPassword;
