import React, { useState } from 'react';
import { useNavigate, Link as RouterLink } from 'react-router-dom';
import {
  Box,
  Button,
  Checkbox,
  FormControlLabel,
  Link,
  TextField,
  Typography,
  InputAdornment,
  IconButton,
  Paper,
  Alert,
} from '@mui/material';
import { Visibility, VisibilityOff, LockOutlined, AccountCircleOutlined } from '@mui/icons-material';
import { toast } from 'react-toastify';

const illustrationPath = '/login-illustration.png';

const Login = () => {
  const [email, setEmail] = useState('admin@demo.com');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleClickShowPassword = () => setShowPassword((show) => !show);
  const handleMouseDownPassword = (event) => event.preventDefault();

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // Check if backend is running first
      const response = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || `Login failed with status: ${response.status}`);
      }

      localStorage.setItem('token', data.token);
      toast.success('Login successful!');
      navigate('/');
    } catch (err) {
      console.error('Login error:', err);

      // Handle specific connection errors
      if (err.message.includes('Failed to fetch') || err.name === 'TypeError') {
        toast.error('Unable to connect to server. Please ensure the backend is running on port 3001.');
      } else {
        const errorMessage = err.message || 'An error occurred during login.';
        toast.error(errorMessage);
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#f0f4f8',
        p: 2,
      }}
    >
      {/* Single unified Paper container */}
      <Paper
        elevation={5}
        sx={{
          display: 'flex',
          maxWidth: '1000px',
          width: '100%',
          minHeight: '600px',
          borderRadius: '16px',
          overflow: 'hidden',
          // Responsive: stack vertically on mobile
          flexDirection: { xs: 'column', md: 'row' },
        }}
      >
        {/* Login Form - Left Side */}
        <Box
          sx={{
            flex: 1,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            p: { xs: 3, sm: 5 },
            backgroundColor: '#ffffff',
            minHeight: { xs: '400px', md: 'auto' },
          }}
        >
          <Box sx={{ maxWidth: '400px', width: '100%' }}>
            <Typography component="h1" variant="h4" sx={{ mb: 3, fontWeight: 'bold', textAlign: 'center' }}>
              Sign In
            </Typography>

            {/* Connection Status Alert */}
            <Alert severity="info" sx={{ mb: 2, fontSize: '0.875rem' }}>
              Make sure your backend server is running on http://localhost:3001
            </Alert>

            <Box component="form" noValidate onSubmit={handleLogin}>
              <TextField
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                InputProps={{ 
                  startAdornment: (
                    <InputAdornment position="start">
                      <AccountCircleOutlined />
                    </InputAdornment>
                  )
                }}
              />
              <TextField
                margin="normal"
                required
                fullWidth
                name="password"
                label="Password"
                type={showPassword ? 'text' : 'password'}
                id="password"
                autoComplete="current-password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <LockOutlined />
                    </InputAdornment>
                  ),
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton 
                        onClick={handleClickShowPassword} 
                        onMouseDown={handleMouseDownPassword} 
                        edge="end"
                        aria-label="toggle password visibility"
                      >
                        {showPassword ? <VisibilityOff /> : <Visibility />}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', my: 2 }}>
                <FormControlLabel 
                  control={
                    <Checkbox 
                      checked={rememberMe} 
                      onChange={(e) => setRememberMe(e.target.checked)} 
                      sx={{ 
                        color: '#d32f2f', 
                        '&.Mui-checked': { color: '#d32f2f' } 
                      }} 
                    />
                  } 
                  label="Remember Me" 
                />
                <Link 
                  component={RouterLink} 
                  to="/forgot-password" 
                  variant="body2" 
                  sx={{ 
                    fontWeight: 'bold', 
                    color: '#d32f2f', 
                    textDecoration: 'none' 
                  }}
                >
                  Forgot Password?
                </Link>
              </Box>
              <Button 
                type="submit" 
                fullWidth 
                variant="contained" 
                disabled={isLoading}
                sx={{ 
                  py: 1.5, 
                  fontSize: '1rem', 
                  fontWeight: 'bold', 
                  backgroundColor: '#d32f2f', 
                  '&:hover': { backgroundColor: '#b71c1c' },
                  '&:disabled': { backgroundColor: '#ccc' }
                }}
              >
                {isLoading ? 'LOGGING IN...' : 'LOGIN'}
              </Button>
              <Typography sx={{ color: 'text.secondary', textAlign: 'center', mt: 3 }}>
                {"Don't have an account?"}{' '}
                <Link 
                  component={RouterLink} 
                  to="/register" 
                  variant="body2" 
                  sx={{ 
                    fontWeight: 'bold', 
                    color: '#d32f2f', 
                    textDecoration: 'none' 
                  }}
                >
                  Create One
                </Link>
              </Typography>
            </Box>
          </Box>
        </Box>

        {/* Illustration - Right Side */}
        <Box
          sx={{
            flex: 1,
            display: { xs: 'none', md: 'flex' },
            alignItems: 'center',
            justifyContent: 'center',
            backgroundColor: '#f8fafc',
            p: 4,
          }}
        >
          <Box
            component="img"
            src={illustrationPath}
            alt="Sign In Illustration"
            sx={{ 
              maxWidth: '100%', 
              maxHeight: '100%',
              height: 'auto',
              objectFit: 'contain'
            }}
          />
        </Box>
      </Paper>
    </Box>
  );
};

export default Login;
