import React, { useState, useEffect, useRef } from 'react';
import {
    Modal, Box, Typography, TextField, IconButton, Avatar, Divider, Paper
} from '@mui/material';
import { Close as CloseIcon, Send as SendIcon, SupportAgent as SupportAgentIcon } from '@mui/icons-material';
import { toast } from 'react-toastify';

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    borderRadius: '16px',
    boxShadow: 24,
    display: 'flex',
    flexDirection: 'column',
    height: '70vh',
    maxHeight: '550px'
};

const initialMessage = {
    id: 1,
    sender: 'bot',
    text: 'Welcome to Live Chat! Please describe your issue below, and a support agent will be with you shortly.'
};

function LiveChatModal({ open, onClose }) {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([initialMessage]);
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    useEffect(() => {
        if (open) {
            setMessages([initialMessage]);
        }
    }, [open]);

    const handleSendMessage = () => {
        if (!message.trim()) return;

        const userMessage = {
            id: Date.now(),
            sender: 'user',
            text: message,
        };
        setMessages(prev => [...prev, userMessage]);
        setMessage('');

        setTimeout(() => {
            const botResponse = {
                id: Date.now() + 1,
                sender: 'bot',
                text: "Thank you for your message. We are reviewing your query and will connect you with a live agent as soon as possible.",
            };
            setMessages(prev => [...prev, botResponse]);
        }, 1200);
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter' && !event.shiftKey) {
            event.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <Modal open={open} onClose={onClose}>
            <Box sx={style}>
                <Box sx={{ p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center', bgcolor: '#f5f5f5', borderTopLeftRadius: '16px', borderTopRightRadius: '16px' }}>
                    <Typography variant="h6" component="h2" sx={{ fontWeight: 'bold' }}>
                        Live Chat Support
                    </Typography>
                    <IconButton onClick={onClose}>
                        <CloseIcon />
                    </IconButton>
                </Box>
                <Box sx={{ flexGrow: 1, p: 2, overflowY: 'auto' }}>
                    {messages.map((msg) => (
                        <Box
                            key={msg.id}
                            sx={{
                                display: 'flex',
                                justifyContent: msg.sender === 'user' ? 'flex-end' : 'flex-start',
                                mb: 2,
                            }}
                        >
                            {msg.sender === 'bot' && (
                                <Avatar sx={{ bgcolor: 'primary.main', mr: 1.5 }}>
                                    <SupportAgentIcon />
                                </Avatar>
                            )}
                            <Paper
                                elevation={0}
                                sx={{
                                    p: 1.5,
                                    borderRadius: '12px',
                                    bgcolor: msg.sender === 'user' ? 'primary.main' : '#f0f0f0',
                                    color: msg.sender === 'user' ? 'white' : 'text.primary',
                                    maxWidth: '80%',
                                }}
                            >
                                <Typography variant="body2">{msg.text}</Typography>
                            </Paper>
                        </Box>
                    ))}
                    <div ref={messagesEndRef} />
                </Box>
                <Divider />
                <Box sx={{ p: 2, bgcolor: '#f5f5f5', borderBottomLeftRadius: '16px', borderBottomRightRadius: '16px' }}>
                    <TextField
                        fullWidth
                        multiline
                        maxRows={3}
                        variant="outlined"
                        placeholder="Type your message..."
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        onKeyPress={handleKeyPress}
                        InputProps={{
                            endAdornment: (
                                <IconButton color="primary" onClick={handleSendMessage} disabled={!message.trim()}>
                                    <SendIcon />
                                </IconButton>
                            ),
                            sx: { borderRadius: '12px', bgcolor: 'white' }
                        }}
                    />
                </Box>
            </Box>
        </Modal>
    );
}

export default LiveChatModal;
