import React, { useState, useEffect } from 'react';
import { Modal, Box, Typography, TextField, Button } from '@mui/material';

function CategoryEditModal({ open, onClose, onSave, itemToEdit, categoryType }) {
    const [name, setName] = useState('');

    useEffect(() => {
        if (itemToEdit) {
            setName(itemToEdit.name);
        } else {
            setName('');
        }
    }, [itemToEdit, open]);

    const handleSave = () => {
        if (!name.trim()) return;
        onSave({ ...itemToEdit, name });
    };

    const modalTitle = `${itemToEdit ? 'Edit' : 'Add'} ${categoryType}`;

    return (
        <Modal open={open} onClose={onClose}>
            <Box sx={{
                position: 'absolute',
                top: '50%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
                width: 400,
                bgcolor: '#2c2c2c',
                border: '2px solid #ff5722',
                borderRadius: '12px',
                boxShadow: 24,
                p: 4,
                color: '#fff',
            }}>
                <Typography variant="h6" component="h2" sx={{ mb: 2, fontWeight: 'bold' }}>
                    {modalTitle}
                </Typography>
                <TextField
                    fullWidth
                    variant="outlined"
                    label={`${categoryType} Name`}
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    sx={{
                        mb: 2,
                        '& .MuiOutlinedInput-root': {
                            '& fieldset': { borderColor: '#ff5722' },
                            '&:hover fieldset': { borderColor: '#e64a19' },
                        },
                        '& .MuiInputLabel-root': { color: '#ccc' },
                        input: { color: '#fff' },
                    }}
                />
                <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 1 }}>
                    <Button onClick={onClose} sx={{ color: '#ccc' }}>Cancel</Button>
                    <Button onClick={handleSave} sx={{ backgroundColor: '#ff5722', color: '#fff', '&:hover': { backgroundColor: '#e64a19' } }}>
                        Save
                    </Button>
                </Box>
            </Box>
        </Modal>
    );
}

export default CategoryEditModal;
