import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Box, TextField, Button, Typography, Link as MuiLink, Paper, Checkbox, FormControlLabel, IconButton, InputAdornment } from '@mui/material';
import AccountCircleOutlinedIcon from '@mui/icons-material/AccountCircleOutlined';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import FacebookIcon from '@mui/icons-material/Facebook';
import GoogleIcon from '@mui/icons-material/Google';
import TwitterIcon from '@mui/icons-material/Twitter';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import { toast } from 'react-toastify';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleClickShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.message || 'Failed to login');
      }

      if (rememberMe) {
        localStorage.setItem('rememberedUsername', username);
        localStorage.setItem('rememberMe', 'true');
      } else {
        localStorage.removeItem('rememberedUsername');
        localStorage.removeItem('rememberMe');
      }

      localStorage.setItem('token', data.token);
      toast.success('Login successful!');
      navigate('/');
    } catch (err) {
      toast.error(err.message);
    }
  };

  return (
    <div className="auth-layout">
      <Paper elevation={4} sx={{ borderRadius: 4, display: 'flex', maxWidth: '900px', width: '100%' }}>
        <Box sx={{ flex: 1, p: { xs: 3, md: 5 }, display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
          <Typography variant="h4" component="h1" sx={{ fontWeight: 'bold', mb: 4 }}>Sign In</Typography>
          <Box component="form" onSubmit={handleLogin} autoComplete="on">
            <TextField
              placeholder="Enter Username or Email"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              autoComplete="username"
              name="username"
              id="username"
              InputProps={{ startAdornment: (<InputAdornment position="start"><AccountCircleOutlinedIcon /></InputAdornment>) }}
              required
              fullWidth
              sx={{ mb: 2 }}
            />
            <TextField
              placeholder="Enter Password"
              type={showPassword ? 'text' : 'password'}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              autoComplete="current-password"
              name="password"
              id="password"
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <LockOutlinedIcon />
                  </InputAdornment>
                ),
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton
                      aria-label="toggle password visibility"
                      onClick={handleClickShowPassword}
                      onMouseDown={handleMouseDownPassword}
                      edge="end"
                    >
                      {showPassword ? <VisibilityOff /> : <Visibility />}
                    </IconButton>
                  </InputAdornment>
                )
              }}
              required
              fullWidth
              sx={{ mb: 2 }}
            />
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={rememberMe}
                    onChange={(e) => setRememberMe(e.target.checked)}
                  />
                }
                label="Remember Me"
                sx={{ color: 'text.secondary' }}
              />
              <MuiLink component={Link} to="/forgot-password" sx={{ fontWeight: 'bold' }}>
                Forgot Password?
              </MuiLink>
            </Box>
            <Button type="submit" variant="contained" color="primary" size="large" fullWidth>Login</Button>
          </Box>
          <Typography sx={{ my: 3, textAlign: 'center', color: 'text.secondary' }}>Or, Login with</Typography>
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2 }}>
            <IconButton><FacebookIcon color="primary" /></IconButton>
            <IconButton><GoogleIcon color="error" /></IconButton>
            <IconButton><TwitterIcon color="info" /></IconButton>
          </Box>
          <Typography sx={{ mt: 3, textAlign: 'center', color: 'text.secondary' }}>
            Don't have an account? <MuiLink component={Link} to="/register" color="secondary" sx={{ fontWeight: 'bold' }}>Create One</MuiLink>
          </Typography>
        </Box>
        <Box sx={{ flex: 1, display: { xs: 'none', md: 'flex' }, alignItems: 'center', justifyContent: 'center' }}>
          <Box
            component="img"
            src="/signin-illustration.png"
            alt="Sign in illustration"
            sx={{ maxWidth: '80%', height: 'auto' }}
            onError={(e) => { e.target.style.display = 'none'; }}
          />
        </Box>
      </Paper>
    </div>
  );
}

export default Login;
