import React from 'react';
import { Modal, Box, Typography, TextField, Button, Avatar } from '@mui/material';

function ProfileModal({ open, onClose }) {
  const user = {
    name: 'Harshith Kumar.M',
    email: 'harshith.kumar@example.com',
    role: 'Administrator',
    phone: '+1 234 567 890',
    location: 'San Francisco, CA'
  };

  // Updated styles for a light theme
  const textFieldStyles = {
    mb: 2,
    '& .MuiOutlinedInput-root': {
      '&.Mui-focused fieldset': {
        borderColor: '#1976d2', // Standard MUI blue for focus
      },
    },
    '& .MuiInputLabel-root.Mui-focused': {
      color: '#1976d2', // Standard MUI blue for focused label
    },
  };

  return (
    <Modal open={open} onClose={onClose}>
      <Box sx={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: 'clamp(400px, 40vw, 600px)',
        bgcolor: 'background.paper', // Changed from dark grey to white
        border: 'none', // Removed dark border
        borderRadius: '16px',
        boxShadow: 24,
        p: 4,
        color: 'text.primary', // Changed from white to primary text color (black)
        outline: 'none',
      }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
          <Avatar
            alt={user.name}
            src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=100&q=80"
            sx={{ width: 64, height: 64, mr: 2, border: '2px solid #ff5722' }}
          />
          <Typography variant="h5" sx={{ fontWeight: 'bold' }}>
            Edit Profile
          </Typography>
        </Box>

        <TextField fullWidth label="Full Name" defaultValue={user.name} variant="outlined" sx={textFieldStyles} />
        <TextField fullWidth label="Email" defaultValue={user.email} variant="outlined" sx={textFieldStyles} />
        <TextField fullWidth label="Role" defaultValue={user.role} variant="outlined" sx={textFieldStyles} />
        <TextField fullWidth label="Phone Number" defaultValue={user.phone} variant="outlined" sx={textFieldStyles} />
        <TextField fullWidth label="Location" defaultValue={user.location} variant="outlined" sx={textFieldStyles} />

        <Box sx={{ display: 'flex', justifyContent: 'flex-start', gap: 2, mt: 3 }}>
          {/* Updated Save Changes button to a primary color */}
          <Button sx={{ 
            bgcolor: '#1976d2', 
            color: '#fff', 
            '&:hover': { bgcolor: '#1565c0' },
            textTransform: 'none',
            px: 3,
          }}>
            Save Changes
          </Button>
          {/* Updated Cancel button to a secondary, light grey style */}
          <Button 
            onClick={onClose} 
            sx={{ 
              bgcolor: '#e0e0e0', 
              color: '#000', 
              '&:hover': { bgcolor: '#d5d5d5' },
              textTransform: 'none',
              px: 3,
            }}
          >
            Cancel
          </Button>
        </Box>
      </Box>
    </Modal>
  );
}

export default ProfileModal;
