import React, { useState } from 'react';
import { Box, Typography, Button, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, IconButton } from '@mui/material';
import { Add as AddIcon, Edit as EditIcon, Delete as DeleteIcon } from '@mui/icons-material';
import CategoryEditModal from '../components/CategoryEditModal';

const initialStatuses = [
    { id: 1, name: 'Not Started' },
    { id: 2, name: 'In Progress' },
    { id: 3, name: 'Completed' },
];

const initialPriorities = [
    { id: 1, name: 'Low' },
    { id: 2, name: 'Moderate' },
    { id: 3, name: 'Extreme' },
];

const pageStyles = {
    backgroundColor: '#1a1a1a',
    minHeight: '100vh',
    p: 4,
    color: '#fff',
};

const headerStyles = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    mb: 2,
    borderBottom: '1px solid #ff5722',
    pb: 1,
};

const buttonStyles = {
    backgroundColor: '#ff5722',
    color: '#fff',
    '&:hover': { backgroundColor: '#e64a19' },
    borderRadius: '8px',
    textTransform: 'none',
};

const tableContainerStyles = {
    backgroundColor: '#2c2c2c',
    borderRadius: '12px',
    border: '1px solid #444',
};

const tableCellStyles = {
    color: '#fff',
    borderColor: '#444',
};

function TaskCategories() {
    const [statuses, setStatuses] = useState(initialStatuses);
    const [priorities, setPriorities] = useState(initialPriorities);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentItem, setCurrentItem] = useState(null);
    const [categoryType, setCategoryType] = useState(''); // 'Status' or 'Priority'

    const handleOpenModal = (item, type) => {
        setCurrentItem(item);
        setCategoryType(type);
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setCurrentItem(null);
        setCategoryType('');
    };

    const handleSave = (item) => {
        if (categoryType === 'Status') {
            const list = statuses;
            const setList = setStatuses;
            if (item.id) {
                setList(list.map(i => (i.id === item.id ? item : i)));
            } else {
                const newItem = { ...item, id: Date.now() };
                setList([...list, newItem]);
            }
        } else if (categoryType === 'Priority') {
            const list = priorities;
            const setList = setPriorities;
            if (item.id) {
                setList(list.map(i => (i.id === item.id ? item : i)));
            } else {
                const newItem = { ...item, id: Date.now() };
                setList([...list, newItem]);
            }
        }
        handleCloseModal();
    };

    const handleDelete = (id, type) => {
        if (window.confirm('Are you sure you want to delete this item?')) {
            if (type === 'Status') {
                setStatuses(statuses.filter(item => item.id !== id));
            } else if (type === 'Priority') {
                setPriorities(priorities.filter(item => item.id !== id));
            }
        }
    };

    const renderTable = (title, data, type) => (
        <Box mb={5}>
            <Box sx={headerStyles}>
                <Typography variant="h5" sx={{ fontWeight: 'bold' }}>{title}</Typography>
                <Button startIcon={<AddIcon />} sx={buttonStyles} onClick={() => handleOpenModal(null, type)}>
                    Add New {type}
                </Button>
            </Box>
            <TableContainer component={Paper} sx={tableContainerStyles}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell sx={tableCellStyles}>ID</TableCell>
                            <TableCell sx={tableCellStyles}>Name</TableCell>
                            <TableCell align="right" sx={tableCellStyles}>Actions</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {data.map((item) => (
                            <TableRow key={item.id}>
                                <TableCell sx={tableCellStyles}>{item.id}</TableCell>
                                <TableCell sx={tableCellStyles}>{item.name}</TableCell>
                                <TableCell align="right" sx={tableCellStyles}>
                                    <Button sx={buttonStyles} startIcon={<EditIcon />} onClick={() => handleOpenModal(item, type)}>Edit</Button>
                                    <Button sx={{ ...buttonStyles, ml: 1, backgroundColor: '#d32f2f', '&:hover': { backgroundColor: '#c62828' } }} startIcon={<DeleteIcon />} onClick={() => handleDelete(item.id, type)}>Delete</Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </Box>
    );

    return (
        <Box sx={pageStyles}>
            <Typography variant="h4" sx={{ mb: 4, fontWeight: 'bold' }}>Manage Categories</Typography>
            <Button sx={{ ...buttonStyles, mb: 4 }}>Add Category</Button>

            {renderTable('Task Status', statuses, 'Status')}
            {renderTable('Priorities', priorities, 'Priority')}

            <CategoryEditModal
                open={isModalOpen}
                onClose={handleCloseModal}
                onSave={handleSave}
                itemToEdit={currentItem}
                categoryType={categoryType}
            />
        </Box>
    );
}

export default TaskCategories;
