import React, { useState } from 'react';
import {
  Modal,
  Box,
  Typography,
  TextField,
  Button,
  IconButton
} from '@mui/material';
import { Close as CloseIcon, Send as SendIcon } from '@mui/icons-material';
import './InviteModal.css'; // We will create this CSS file next

function InviteModal({ open, onClose, onInvite }) {
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');

  const handleSendInvite = () => {
    // Basic email validation
    if (!email || !/\S+@\S+\.\S+/.test(email)) {
      setError('Please enter a valid email address.');
      return;
    }
    onInvite(email);
    handleClose();
  };

  const handleClose = () => {
    setEmail('');
    setError('');
    onClose();
  };

  return (
    <Modal open={open} onClose={handleClose}>
      <Box className="invite-modal-container">
        <Box className="invite-modal-header">
          <Typography variant="h6" className="invite-modal-title">Invite Team Members</Typography>
          <IconButton onClick={handleClose}>
            <CloseIcon />
          </IconButton>
        </Box>
        <Box className="invite-modal-body">
          <Typography variant="body2" className="invite-modal-subtitle">
            Enter the email address of the team member you want to invite.
          </Typography>
          <TextField
            fullWidth
            label="Email Address"
            variant="outlined"
            value={email}
            onChange={(e) => {
              setEmail(e.target.value);
              if (error) setError('');
            }}
            error={!!error}
            helperText={error}
            className="invite-modal-textfield"
            placeholder="e.g., teammate@example.com"
          />
        </Box>
        <Box className="invite-modal-footer">
          <Button variant="text" onClick={handleClose} className="cancel-btn">
            Cancel
          </Button>
          <Button
            variant="contained"
            onClick={handleSendInvite}
            className="send-invite-btn"
            startIcon={<SendIcon />}
          >
            Send Invite
          </Button>
        </Box>
      </Box>
    </Modal>
  );
}

export default InviteModal;
