import React, { useState, useEffect } from 'react';
import { Box, Typography, Card, CardContent, IconButton } from '@mui/material';
import { useDashboardContext } from './DashboardLayout';
import { Delete as DeleteIcon, Edit as EditIcon, MoreVert as MoreVertIcon, RadioButtonChecked } from '@mui/icons-material';

// New, professional sample data for the "My Task" page
const initialMyTasks = [
  {
    id: 201,
    title: "Submit Documents",
    shortDescription: "Make sure to submit all the necessary docum.....",
    priority: "Extreme",
    status: "Not Started",
    createdOn: "20/06/2023",
    image: "https://images.unsplash.com/photo-1583324113620-9152b11b8b80?fit=crop&w=600&h=400",
    objective: "To submit required documents for something important",
    taskDescription: "Review the list of documents required for submission and ensure all necessary documents are ready. Organize the documents accordingly and scan them if physical copies need to be submitted digitally. Rename the scanned files appropriately for easy identification and verify the accepted file formats. Upload the documents securely to the designated platform, double-check for accuracy, and obtain confirmation of successful submission. Follow up if necessary to ensure proper processing.",
    additionalNotes: [
      "Ensure that the documents are authentic and up-to-date.",
      "Maintain confidentiality and security of sensitive information during the submission process.",
      "If there are specific guidelines or deadlines for submission, adhere to them diligently."
    ],
    deadline: "End of Day"
  },
  {
    id: 202,
    title: "Complete Assignments",
    shortDescription: "The assignments must be completed to pass final year....",
    priority: "Moderate",
    status: "In Progress",
    createdOn: "20/06/2023",
    image: "https://images.unsplash.com/photo-1517842645767-c6f90405774b?fit=crop&w=600&h=400",
    objective: "To complete all pending academic assignments for the semester.",
    taskDescription: "Break down each assignment into smaller, manageable tasks. Allocate specific time slots for each assignment, prioritizing the ones with nearer deadlines. Gather all necessary resources, including textbooks, research papers, and lecture notes.",
    additionalNotes: [
        "Proofread all assignments for grammatical errors and plagiarism before submission.",
        "Ensure all citation and formatting guidelines are met."
    ],
    deadline: "End of Week"
  },
  {
    id: 203,
    title: "Client Follow-up",
    shortDescription: "Follow up with the new client about the proposal....",
    priority: "Moderate",
    status: "Not Started",
    createdOn: "22/06/2023",
    image: "https://images.unsplash.com/photo-1556740738-b6a63e27c4df?fit=crop&w=600&h=400",
    objective: "To secure a partnership with the new client.",
    taskDescription: "Draft a polite and professional follow-up email regarding the business proposal sent last week. Reiterate the key benefits of the proposal and express eagerness to answer any questions they might have.",
    additionalNotes: [
      "Attach a summarized version of the proposal for their convenience.",
      "Suggest a brief call to discuss the proposal in more detail."
    ],
    deadline: "Tomorrow"
  }
];

// Helper to get priority color, matching the new design
const getPriorityColor = (priority) => {
    if (priority === 'Extreme') return '#ff5757';
    if (priority === 'Moderate') return '#3ABEFE';
    return '#4caf50'; // Low
};

function MyTask() {
  const { searchTerm } = useDashboardContext();
  const [tasks, setTasks] = useState(initialMyTasks);
  const [selectedTask, setSelectedTask] = useState(null);

  const filteredTasks = tasks.filter(task =>
    task.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    task.shortDescription.toLowerCase().includes(searchTerm.toLowerCase())
  );

  useEffect(() => {
    if (selectedTask && !filteredTasks.some(t => t.id === selectedTask.id)) {
        setSelectedTask(filteredTasks.length > 0 ? filteredTasks[0] : null);
    } else if (!selectedTask && filteredTasks.length > 0) {
        setSelectedTask(filteredTasks[0]);
    }
  }, [searchTerm, filteredTasks, selectedTask]);

  // Placeholder functions for buttons. A real implementation would open a modal.
  const handleEdit = (task) => alert(`Editing: ${task.title}`);
  const handleDelete = (task) => {
      if (window.confirm(`Are you sure you want to delete "${task.title}"?`)) {
          const newTasks = tasks.filter(t => t.id !== task.id);
          setTasks(newTasks);
      }
  };

  return (
    <Box sx={{ display: 'flex', height: '100%', gap: 3, p: 0, bgcolor: '#1a1a1a', color: '#fff' }}>

      {/* Left Column: Task List */}
      <Box sx={{ width: '450px', flexShrink: 0, display: 'flex', flexDirection: 'column', gap: 3, overflowY: 'auto', pr: 2 }}>
        {filteredTasks.length > 0 ? (
          filteredTasks.map(task => (
            <Card 
              key={task.id}
              onClick={() => setSelectedTask(task)}
              elevation={2}
              sx={{
                bgcolor: '#2c2c2e', color: '#fff', borderRadius: '20px', cursor: 'pointer',
                border: '2px solid', borderColor: selectedTask?.id === task.id ? getPriorityColor(task.priority) : 'transparent',
                transition: 'all 0.3s ease', '&:hover': { transform: 'translateY(-4px)' }
              }}
            >
              <CardContent sx={{ p: '24px !important', display: 'grid', gridTemplateColumns: '1fr auto', gap: '12px 16px', alignItems: 'center' }}>
                <Box sx={{ gridColumn: '1 / 2', display: 'flex', alignItems: 'center', gap: 1.5 }}>
                  <RadioButtonChecked sx={{ color: getPriorityColor(task.priority) }} />
                  <Typography variant="h6" sx={{ fontSize: '1.2rem', fontWeight: 600 }}>{task.title}</Typography>
                </Box>
                <IconButton sx={{ gridColumn: '2 / 3', gridRow: '1 / 2', color: '#888' }}><MoreVertIcon /></IconButton>
                <Typography variant="body2" sx={{ gridColumn: '1 / 3', color: '#b0b0b0' }}>{task.shortDescription}</Typography>
                <Box sx={{ gridColumn: '2 / 3', gridRow: '2 / 4', width: '100px', height: '80px', borderRadius: '12px', overflow: 'hidden', alignSelf: 'center' }}>
                    <img src={task.image} alt={task.title} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                </Box>
                <Box sx={{ gridColumn: '1 / 3', display: 'flex', justifyContent: 'space-between', fontSize: '0.8rem', color: '#b0b0b0', borderTop: '1px solid #444', pt: 1.5, mt: 1 }}>
                  <Typography variant="caption">Priority: <span style={{ color: getPriorityColor(task.priority), fontWeight: 'bold' }}>{task.priority}</span></Typography>
                  <Typography variant="caption">Status: <span style={{ color: task.status === 'In Progress' ? '#3ABEFE' : '#ff5757', fontWeight: 'bold' }}>{task.status}</span></Typography>
                  <Typography variant="caption">Created on: {task.createdOn}</Typography>
                </Box>
              </CardContent>
            </Card>
          ))
        ) : (
          <Typography sx={{ color: 'text.secondary', p: 2, textAlign: 'center' }}>
            No tasks found for "{searchTerm}"
          </Typography>
        )}
      </Box>

      {/* Right Column: Task Details */}
      <Box sx={{ flex: 1, bgcolor: '#111', borderRadius: '20px', p: 4, position: 'relative', overflowY: 'auto' }}>
        {selectedTask ? (
          <>
            <Box sx={{ width: '100%', height: '300px', borderRadius: '16px', mb: 3, overflow: 'hidden' }}>
              <img src={selectedTask.image} alt={selectedTask.title} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
            </Box>
            <Box sx={{ display: 'flex', gap: 3, color: '#b0b0b0', mb: 2 }}>
              <Typography variant="body2">Priority: <span style={{ color: getPriorityColor(selectedTask.priority), fontWeight: '600' }}>{selectedTask.priority}</span></Typography>
              <Typography variant="body2">Status: <span style={{ color: selectedTask.status === 'In Progress' ? '#3ABEFE' : '#ff5757', fontWeight: '600' }}>{selectedTask.status}</span></Typography>
              <Typography variant="body2">Created on: {selectedTask.createdOn}</Typography>
            </Box>

            <Typography variant="h5" sx={{ mb: 1.5, fontWeight: 'bold' }}><span style={{color: '#b0b0b0'}}>Task Title:</span> {selectedTask.title}</Typography>
            <Typography variant="h6" sx={{ mb: 1.5, fontWeight: 'medium' }}><span style={{color: '#b0b0b0'}}>Objective:</span> {selectedTask.objective}</Typography>
            <Typography variant="body1" sx={{ mb: 3, lineHeight: 1.7, color: '#e0e0e0' }}><span style={{color: '#b0b0b0'}}>Task Description:</span> {selectedTask.taskDescription}</Typography>

            <Typography variant="h6" sx={{ mb: 1.5, fontWeight: 'medium', color: '#b0b0b0' }}>Additional Notes:</Typography>
            <Box component="ul" sx={{ pl: 3, mb: 3 }}>
              {selectedTask.additionalNotes.map((note, index) => (
                <Typography component="li" key={index} sx={{ color: '#e0e0e0', mb: 1 }}>{note}</Typography>
              ))}
            </Box>

            <Typography variant="h6" sx={{ fontWeight: 'bold' }}><span style={{color: '#b0b0b0'}}>Deadline for Submission:</span> {selectedTask.deadline}</Typography>

            <Box sx={{ position: 'absolute', bottom: 32, right: 32, display: 'flex', gap: 2 }}>
              <IconButton onClick={() => handleDelete(selectedTask)} sx={{ width: 60, height: 60, color: 'white', bgcolor: '#ff6f61', '&:hover': { bgcolor: '#e05252' } }}><DeleteIcon /></IconButton>
              <IconButton onClick={() => handleEdit(selectedTask)} sx={{ width: 60, height: 60, color: 'white', bgcolor: '#2196f3', '&:hover': { bgcolor: '#1976d2' } }}><EditIcon /></IconButton>
            </Box>
          </>
        ) : (
          <Typography sx={{ color: 'text.secondary', textAlign: 'center', mt: 5 }}>Select a task to see the details.</Typography>
        )}
      </Box>
    </Box>
  );
}

export default MyTask;
