import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Box, TextField, Button, Typography, Link as MuiLink, Paper, Checkbox, FormControlLabel, IconButton, InputAdornment } from '@mui/material';
import { AccountCircleOutlined, LockOutlined, Visibility, VisibilityOff } from '@mui/icons-material';
import { toast } from 'react-toastify';

function Login() {
  const [email, setEmail] = useState('admin@demo.com');
  const [password, setPassword] = useState('demopassword');
  const [rememberMe, setRememberMe] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    document.body.style.backgroundColor = '#f0f4f8';
    return () => {
      document.body.style.backgroundColor = null;
    };
  }, []);

  const handleClickShowPassword = () => setShowPassword(!showPassword);
  const handleMouseDownPassword = (event) => event.preventDefault();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.message || 'Failed to login');
      }

      localStorage.setItem('token', data.token);
      toast.success('Login successful!');
      navigate('/'); 
    } catch (err) {
      toast.error(err.message);
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        p: 2,
      }}
    >
      <Paper
        elevation={5}
        sx={{
          p: 4,
          width: '100%',
          maxWidth: '500px',
          borderRadius: '16px',
          boxShadow: '0 8px 32px 0 rgba(0,0,0,0.1)',
          textAlign: 'center',
        }}
      >
        <Box component="form" onSubmit={handleLogin} noValidate sx={{ textAlign: 'left' }}>
          <Typography variant="h4" component="h1" sx={{ fontWeight: 'bold', mb: 3, textAlign: 'center' }}>
            Sign In
          </Typography>

          <TextField
            fullWidth
            required
            id="email"
            label="Email"
            name="email"
            autoComplete="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            InputProps={{
              startAdornment: (<InputAdornment position="start"><AccountCircleOutlined /></InputAdornment>),
            }}
            sx={{ mb: 2 }}
          />
          <TextField
            fullWidth
            required
            name="password"
            label="Password"
            type={showPassword ? 'text' : 'password'}
            id="password"
            autoComplete="current-password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            InputProps={{
              startAdornment: (<InputAdornment position="start"><LockOutlined /></InputAdornment>),
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={handleClickShowPassword} onMouseDown={handleMouseDownPassword} edge="end">
                    {showPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
            sx={{ mb: 2 }}
          />
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <FormControlLabel
              control={
                <Checkbox
                  checked={rememberMe}
                  onChange={(e) => setRememberMe(e.target.checked)}
                  sx={{ color: '#d32f2f', '&.Mui-checked': { color: '#d32f2f' } }}
                />
              }
              label="Remember Me"
            />
            <MuiLink component={Link} to="/forgot-password" sx={{ fontWeight: 'bold', color: '#d32f2f', textDecoration: 'none' }}>
              Forgot Password?
            </MuiLink>
          </Box>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{
              py: 1.5,
              backgroundColor: '#d32f2f',
              '&:hover': { backgroundColor: '#b71c1c' },
              fontSize: '1rem',
              fontWeight: 'bold',
            }}
          >
            LOGIN
          </Button>
        </Box>

        <Typography sx={{ mt: 3, color: 'text.secondary' }}>
          Don't have an account?{' '}
          <MuiLink component={Link} to="/register" sx={{ fontWeight: 'bold', color: '#d32f2f', textDecoration: 'none' }}>
            Create One
          </MuiLink>
        </Typography>

        <Box
          component="img"
          src="/signin-illustration.png"
          alt="Sign in illustration"
          sx={{ maxWidth: '80%', height: 'auto', mt: 4 }}
        />
      </Paper>
    </Box>
  );
}

export default Login;
