import { createTheme } from '@mui/material/styles';

export const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#D32F2F', // Your primary red
    },
    background: {
      default: '#121212', // Main background
      paper: '#1e1e1e',   // Card and header background
    },
    text: {
      primary: '#e0e0e0',   // Main text
      secondary: '#b0bec5', // Lighter, secondary text
    },
    divider: '#424242', // For borders and dividers
  },
  components: {
    // This ensures cards use the correct background and borders
    MuiCard: {
      styleOverrides: {
        root: {
          backgroundColor: '#1e1e1e',
          border: '1px solid #424242',
        },
      },
    },
    // This ensures text field outlines are visible
    MuiOutlinedInput: {
      styleOverrides: {
        notchedOutline: {
          borderColor: '#424242',
        },
      },
    },
  },
});
