import React, { useState, useEffect } from 'react';
import { Modal, Box, Typography, TextField, Button, Avatar } from '@mui/material';

function ProfileModal({ open, onClose, user, onSave }) {
  const [formData, setFormData] = useState(user);

  useEffect(() => {
    if (user) {
      setFormData(user);
    }
  }, [user, open]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = () => {
    onSave(formData);
  };

  const textFieldStyles = {
    mb: 2,
    '& .MuiOutlinedInput-root': {
      '&.Mui-focused fieldset': { borderColor: '#1976d2' },
    },
    '& .MuiInputLabel-root.Mui-focused': { color: '#1976d2' },
  };

  return (
    <Modal open={open} onClose={onClose}>
      <Box sx={{
        position: 'absolute', top: '50%', left: '50%',
        transform: 'translate(-50%, -50%)', width: 'clamp(400px, 40vw, 600px)',
        bgcolor: 'background.paper', borderRadius: '16px', boxShadow: 24, p: 4,
        color: 'text.primary', outline: 'none',
      }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
          <Avatar alt={formData.name} src={formData.avatar} sx={{ width: 64, height: 64, mr: 2, border: '2px solid #ff5722' }} />
          <Typography variant="h5" sx={{ fontWeight: 'bold' }}>Edit Profile</Typography>
        </Box>

        <TextField fullWidth name="name" label="Full Name" value={formData.name} onChange={handleChange} variant="outlined" sx={textFieldStyles} />
        <TextField fullWidth name="email" label="Email" value={formData.email} onChange={handleChange} variant="outlined" sx={textFieldStyles} />
        <TextField fullWidth name="avatar" label="Avatar URL" value={formData.avatar} onChange={handleChange} variant="outlined" sx={textFieldStyles} />
        <TextField fullWidth name="role" label="Role" value={formData.role} onChange={handleChange} variant="outlined" sx={textFieldStyles} />
        <TextField fullWidth name="phone" label="Phone Number" value={formData.phone} onChange={handleChange} variant="outlined" sx={textFieldStyles} />
        <TextField fullWidth name="location" label="Location" value={formData.location} onChange={handleChange} variant="outlined" sx={textFieldStyles} />

        <Box sx={{ display: 'flex', justifyContent: 'flex-start', gap: 2, mt: 3 }}>
          <Button onClick={handleSave} sx={{ bgcolor: '#1976d2', color: '#fff', '&:hover': { bgcolor: '#1565c0' }, textTransform: 'none', px: 3 }}>
            Save Changes
          </Button>
          <Button onClick={onClose} sx={{ bgcolor: '#e0e0e0', color: '#000', '&:hover': { bgcolor: '#d5d5d5' }, textTransform: 'none', px: 3 }}>
            Cancel
          </Button>
        </Box>
      </Box>
    </Modal>
  );
}

export default ProfileModal;
