import React, { useState, useEffect } from "react";
import {
  Avatar,
  Button,
  TextField,
  FormControlLabel,
  Checkbox,
  Link,
  Grid,
  Box,
  Typography,
  Container,
  Paper
} from "@mui/material";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    document.documentElement.style.backgroundColor = "#FF6767";
    document.body.style.backgroundColor = "#FF6767";

    const removePinkBg = () => {
      const nodes = [...document.querySelectorAll("div, section, main, header, footer")];
      nodes.forEach(node => {
        if (!node) return;
        if (node.classList && (node.classList.contains("form-card") || node.closest(".form-card"))) return;
        const bg = window.getComputedStyle(node).backgroundColor;
        if (!bg || bg === "transparent" || bg === "rgba(0, 0, 0, 0)") return;
        const m = bg.match(/rgb\((\d+),\s*(\d+),\s*(\d+)\)/);
        if (!m) return;
        const r = Number(m[1]), g = Number(m[2]), b = Number(m[3]);
        if (r > 200 && g < 160 && b < 160) {
          node.style.background = "transparent";
          node.style.backgroundColor = "transparent";
        }
      });
    };

    removePinkBg();
    const mo = new MutationObserver(removePinkBg);
    mo.observe(document.body, { childList: true, subtree: true, attributes: true, attributeFilter: ["style", "class"] });
    return () => mo.disconnect();
  }, []);

  const handleSubmit = event => {
    event.preventDefault();
    console.log({ email, password });
  };

  return (
    <Container component="main" maxWidth="xs">
      <Paper
        className="form-card"
        elevation={6}
        sx={{
          marginTop: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          padding: 4,
          borderRadius: 3,
          backgroundColor: "#ffffff"
        }}
      >
        <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign in
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
          <TextField
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
            autoFocus
            value={email}
            onChange={e => setEmail(e.target.value)}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
          <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Remember me"
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2, bgcolor: "#FF6767", "&:hover": { bgcolor: "#e05555" } }}
          >
            Sign In
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
              <Link href="/register" variant="body2">
                {"Don't have an account? Sign Up"}
              </Link>
            </Grid>
          </Grid>
        </Box>
      </Paper>
    </Container>
  );
}
