import React, { useState, useEffect } from 'react';
import { Modal, Box, Typography, TextField, Button, RadioGroup, FormControlLabel, Radio } from '@mui/material';
import { AddPhotoAlternate as UploadIcon } from '@mui/icons-material';
import './AddTaskModal.css'; // We can reuse the same styles

function EditVitalTaskModal({ open, onClose, onUpdateTask, taskToEdit }) {
  const [title, setTitle] = useState('');
  const [priority, setPriority] = useState('Moderate');
  const [description, setDescription] = useState('');
  const [fullDescription, setFullDescription] = useState('');
  const [imagePreview, setImagePreview] = useState('');
  const [error, setError] = useState('');

  // Pre-fill the form when a task is passed to the modal
  useEffect(() => {
    if (taskToEdit) {
      setTitle(taskToEdit.title || '');
      setPriority(taskToEdit.priority || 'Moderate');
      setDescription(taskToEdit.description || '');
      setFullDescription(taskToEdit.fullDescription || '');
      setImagePreview(taskToEdit.image || '');
    }
  }, [taskToEdit]);

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setImagePreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = () => {
    if (!title.trim()) {
      setError('Title is required!');
      return;
    }

    onUpdateTask({
      ...taskToEdit, // Keep the original ID and other properties
      title: title.trim(),
      description: description.trim(),
      fullDescription: fullDescription.trim(),
      priority,
      image: imagePreview,
    });
    onClose();
  };

  return (
    <Modal open={open} onClose={onClose}>
      <Box className="edit-modal-container">
        <Box className="edit-modal-header">
          <Typography variant="h5" className="edit-modal-title">Edit Vital Task</Typography>
          <Button variant="text" onClick={onClose} className="go-back-btn">Cancel</Button>
        </Box>
        <Box className="edit-modal-body">
          <Box className="form-left">
            <Typography variant="subtitle1" className="form-label">Title</Typography>
            <TextField fullWidth variant="outlined" value={title} onChange={(e) => { setTitle(e.target.value); if(error) setError(''); }} error={!!error} helperText={error} />

            <Typography variant="subtitle1" className="form-label">Priority</Typography>
            <RadioGroup row value={priority} onChange={(e) => setPriority(e.target.value)} className="priority-group">
              <FormControlLabel value="Extreme" control={<Radio sx={{color: 'red', '&.Mui-checked': { color: 'red' }}}/>} label="Extreme" />
              <FormControlLabel value="Moderate" control={<Radio sx={{color: '#2196f3', '&.Mui-checked': { color: '#2196f3' }}}/>} label="Moderate" />
              <FormControlLabel value="Low" control={<Radio sx={{color: 'green', '&.Mui-checked': { color: 'green' }}}/>} label="Low" />
            </RadioGroup>

            <Typography variant="subtitle1" className="form-label">Short Description (for card view)</Typography>
            <TextField fullWidth variant="outlined" multiline rows={2} value={description} onChange={(e) => setDescription(e.target.value)} />

            <Typography variant="subtitle1" className="form-label">Full Description (for detail view)</Typography>
            <TextField fullWidth variant="outlined" multiline rows={6} placeholder="Start writing here...." value={fullDescription} onChange={(e) => setFullDescription(e.target.value)} />
          </Box>
          <Box className="form-right">
             <Typography variant="subtitle1" className="form-label">Upload Image</Typography>
             <Box className="upload-area">
                <input accept="image/*" style={{ display: 'none' }} id="edit-button-file" type="file" onChange={handleImageChange} />
                {imagePreview ? (<img src={imagePreview} alt="preview" className="image-preview"/>) : (<Box className="upload-placeholder"><UploadIcon sx={{ fontSize: 48, color: '#ccc' }} /><Typography>Drag&Drop files here</Typography><Typography>or</Typography><label htmlFor="edit-button-file"><Button variant="outlined" component="span" className="browse-btn">Browse</Button></label></Box>)}
             </Box>
          </Box>
        </Box>
        <Box className="edit-modal-footer">
            <Button variant="contained" onClick={handleSubmit} className="done-btn">Update Task</Button>
        </Box>
      </Box>
    </Modal>
  );
}

export default EditVitalTaskModal;
