import React, { useState } from 'react';
import { useNavigate, Link as RouterLink } from 'react-router-dom';
import {
  Box,
  Button,
  Checkbox,
  FormControlLabel,
  Link,
  TextField,
  Typography,
  InputAdornment,
  IconButton,
  Paper,
  Grid,
} from '@mui/material';
import { Visibility, VisibilityOff, LockOutlined, AccountCircleOutlined } from '@mui/icons-material';
import { toast } from 'react-toastify';

const illustrationPath = '/login-illustration.png';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleClickShowPassword = () => setShowPassword((show) => !show);
  const handleMouseDownPassword = (event) => event.preventDefault();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || `Login failed with status: ${response.status}`);
      }

      localStorage.setItem('token', data.token);
      toast.success('Login successful!');
      navigate('/');
    } catch (err) {
      const errorMessage = err.message || 'An error occurred during login.';
      toast.error(errorMessage);
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#f0f4f8',
        p: 2,
      }}
    >
      <Box
        sx={{
          maxWidth: '1200px',
          width: '100%',
          display: 'flex',
          alignItems: 'stretch',
          minHeight: '600px',
          gap: 0,
        }}
      >
        {/* Login Form - Left Side */}
        <Box
          sx={{
            flex: 1,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <Paper
            elevation={5}
            sx={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              p: { xs: 3, sm: 5 },
              borderRadius: '16px',
              width: '100%',
              maxWidth: '450px',
              height: '100%',
              minHeight: '600px',
            }}
          >
            <Box sx={{ maxWidth: '400px', width: '100%' }}>
              <Typography component="h1" variant="h4" sx={{ mb: 3, fontWeight: 'bold', textAlign: 'center' }}>
                Sign In
              </Typography>
              <Box component="form" noValidate onSubmit={handleLogin}>
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  InputProps={{ 
                    startAdornment: (
                      <InputAdornment position="start">
                        <AccountCircleOutlined />
                      </InputAdornment>
                    )
                  }}
                />
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  type={showPassword ? 'text' : 'password'}
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <LockOutlined />
                      </InputAdornment>
                    ),
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton 
                          onClick={handleClickShowPassword} 
                          onMouseDown={handleMouseDownPassword} 
                          edge="end"
                        >
                          {showPassword ? <VisibilityOff /> : <Visibility />}
                        </IconButton>
                      </InputAdornment>
                    ),
                  }}
                />
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', my: 2 }}>
                  <FormControlLabel 
                    control={
                      <Checkbox 
                        checked={rememberMe} 
                        onChange={(e) => setRememberMe(e.target.checked)} 
                        sx={{ 
                          color: '#d32f2f', 
                          '&.Mui-checked': { color: '#d32f2f' } 
                        }} 
                      />
                    } 
                    label="Remember Me" 
                  />
                  <Link 
                    component={RouterLink} 
                    to="/forgot-password" 
                    variant="body2" 
                    sx={{ 
                      fontWeight: 'bold', 
                      color: '#d32f2f', 
                      textDecoration: 'none' 
                    }}
                  >
                    Forgot Password?
                  </Link>
                </Box>
                <Button 
                  type="submit" 
                  fullWidth 
                  variant="contained" 
                  sx={{ 
                    py: 1.5, 
                    fontSize: '1rem', 
                    fontWeight: 'bold', 
                    backgroundColor: '#d32f2f', 
                    '&:hover': { backgroundColor: '#b71c1c' } 
                  }}
                >
                  LOGIN
                </Button>
                <Typography sx={{ color: 'text.secondary', textAlign: 'center', mt: 3 }}>
                  {"Don't have an account?"}{' '}
                  <Link 
                    component={RouterLink} 
                    to="/register" 
                    variant="body2" 
                    sx={{ 
                      fontWeight: 'bold', 
                      color: '#d32f2f', 
                      textDecoration: 'none' 
                    }}
                  >
                    Create One
                  </Link>
                </Typography>
              </Box>
            </Box>
          </Paper>
        </Box>

        {/* Illustration - Right Side */}
        <Box
          sx={{
            flex: 1,
            display: { xs: 'none', md: 'flex' },
            alignItems: 'center',
            justifyContent: 'center',
            backgroundColor: '#ffffff',
            borderRadius: '16px',
            p: 4,
            minHeight: '600px',
          }}
        >
          <Box
            component="img"
            src={illustrationPath}
            alt="Sign In Illustration"
            sx={{ 
              maxWidth: '100%', 
              maxHeight: '100%',
              height: 'auto',
              objectFit: 'contain'
            }}
          />
        </Box>
      </Box>
    </Box>
  );
};

export default Login;
