import React, { useState } from 'react';
import { Outlet, Link, useLocation, useNavigate, useOutletContext } from 'react-router-dom';
import { Box, Typography, IconButton, Avatar, TextField } from '@mui/material';
import {
  Dashboard as DashboardIcon, Settings as SettingsIcon, LightbulbOutlined as LightbulbOutlinedIcon,
  TaskAlt as TaskAltIcon, Segment as SegmentIcon, HelpOutline as HelpOutlineIcon, ExitToApp as LogoutIcon,
  Search as SearchIcon, Notifications as NotificationsIcon, CalendarToday as CalendarIcon
} from '@mui/icons-material';
import './Dashboard.css';
import CalendarPopover from './CalendarPopover';
import ProfileModal from '../components/ProfileModal';

function DashboardLayout() {
  const location = useLocation();
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [isCalendarOpen, setCalendarOpen] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [isProfileModalOpen, setProfileModalOpen] = useState(false);

  const handleDateChange = (date) => {
    setSelectedDate(date);
    setCalendarOpen(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const sidebarItems = [
    { path: '/', icon: <DashboardIcon />, label: 'Dashboard' },
    { path: '/vital-task', icon: <LightbulbOutlinedIcon />, label: 'Vital Task' },
    { path: '/my-task', icon: <TaskAltIcon />, label: 'My Task' },
    { path: '/task-categories', icon: <SegmentIcon />, label: 'Task Categories' },
    { path: '/settings', icon: <SettingsIcon />, label: 'Settings' },
    { path: '/help', icon: <HelpOutlineIcon />, label: 'Help' }
  ];

  return (
    <>
      <ProfileModal open={isProfileModalOpen} onClose={() => setProfileModalOpen(false)} />
      <Box className="dashboard-container">
        <Box className="sidebar">
          <Box sx={{ textAlign: 'center', cursor: 'pointer' }} onClick={() => setProfileModalOpen(true)}>
            <Avatar className="user-avatar-v2">H</Avatar>
            <Typography variant="h6" className="user-name-v2">Harshith Kumar.M</Typography>
            <Typography variant="body2" className="user-email-v2">harshith.kumar@example.com</Typography>
          </Box>

          <Box className="sidebar-menu-v2">
            {sidebarItems.map((item) => (
              <Link to={item.path} key={item.label} className="menu-item-link">
                <Box className={`menu-item-v2 ${location.pathname === item.path ? 'active' : ''}`}>
                  {item.icon} <Typography>{item.label}</Typography>
                </Box>
              </Link>
            ))}
          </Box>

          <Box className="logout-section-v2" onClick={handleLogout}>
            <LogoutIcon /> <Typography>Logout</Typography>
          </Box>
        </Box>

        <Box className="content-area-wrapper">
          <CalendarPopover open={isCalendarOpen} selectedDate={selectedDate} onChange={handleDateChange} onClose={() => setCalendarOpen(false)} />

          <Box className="header">
              <Typography variant="h4" className="dashboard-title"><span className="dash-text">Dash</span><span className="board-text">board</span></Typography>
              <TextField 
                  className="search-field" 
                  placeholder="Search your task here..." 
                  variant="outlined" 
                  size="small" 
                  value={searchTerm} 
                  onChange={(e) => setSearchTerm(e.target.value)} 
                  InputProps={{ 
                      startAdornment: <SearchIcon className="search-icon" />, 
                      endAdornment: <IconButton className="search-btn" size="small"><SearchIcon /></IconButton> 
                  }}
              />
              <Box className="header-right">
                  <IconButton className="notification-btn"><NotificationsIcon /></IconButton>
                  <Box className="calendar-trigger" onClick={() => setCalendarOpen(!isCalendarOpen)}>
                      <IconButton className="calendar-btn"><CalendarIcon /></IconButton>
                      <Box className="date-display">
                          <Typography className="day">{selectedDate.toLocaleDateString('en-US', { weekday: 'long' })}</Typography>
                          <Typography className="date">{selectedDate.toLocaleDateString('en-GB')}</Typography>
                      </Box>
                  </Box>
              </Box>
          </Box>

          <Outlet context={{ searchTerm, selectedDate }} />
        </Box>
      </Box>
    </>
  );
}

export function useDashboardContext() {
  return useOutletContext();
}

export default DashboardLayout;
