import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Box, TextField, Button, Typography, Link as MuiLink, Paper, Checkbox, FormControlLabel, InputAdornment, IconButton } from '@mui/material';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';
import BadgeOutlinedIcon from '@mui/icons-material/BadgeOutlined';
import AccountCircleOutlinedIcon from '@mui/icons-material/AccountCircleOutlined';
import EmailOutlinedIcon from '@mui/icons-material/EmailOutlined';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import VpnKeyOutlinedIcon from '@mui/icons-material/VpnKeyOutlined';
import PhoneIcon from '@mui/icons-material/Phone';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import { toast } from 'react-toastify';

function Register() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [agreeTerms, setAgreeTerms] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    setFirstName('Harshith');
    setLastName('Kumar');
    setUsername('harshith.kumar');
    setEmail('harshith.kumar@example.com');
    setPhone('+91 98765 43210');
    setPassword('password123');
    setConfirmPassword('password123');
    setAgreeTerms(true);
  }, []);

  const handleClickShowPassword = () => setShowPassword(!showPassword);
  const handleClickShowConfirmPassword = () => setShowConfirmPassword(!showConfirmPassword);
  const handleMouseDownPassword = (event) => event.preventDefault();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      toast.error("Passwords do not match.");
      return;
    }
    if (!agreeTerms) {
      toast.error("Please agree to the terms and conditions.");
      return;
    }
    try {
      const response = await fetch('http://localhost:3001/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ firstName, lastName, username, email, phone, password }),
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.message || 'Failed to register');
      }
      toast.success('Registration successful! Redirecting to login...');
      setTimeout(() => navigate('/login'), 2000);
    } catch (err) {
      toast.error(err.message);
    }
  };

  return (
    <Paper
      elevation={4}
      sx={{
        width: '100%',
        maxWidth: '900px',
        borderRadius: 4,
        display: 'flex',
        overflow: 'hidden'
      }}
    >
      <Box sx={{ flex: 1, display: { xs: 'none', md: 'flex' }, alignItems: 'center', justifyContent: 'center', p: 4, background: '#f5f7fa' }}>
        <Box
          component="img"
          src="/R.png"
          alt="Registration illustration"
          sx={{ maxWidth: '100%', maxHeight: '100%', objectFit: 'contain' }}
        />
      </Box>
      <Box sx={{ flex: 1, p: { xs: 3, md: 5 }, display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
        <Typography variant="h4" component="h1" sx={{ fontWeight: 'bold', mb: 4 }}>Sign Up</Typography>
        <Box component="form" onSubmit={handleSubmit}>
          <TextField
            placeholder="Enter First Name"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            InputProps={{ startAdornment: (<InputAdornment position="start"><PersonOutlineIcon /></InputAdornment>) }}
            required fullWidth sx={{ mb: 2 }}
          />
          <TextField
            placeholder="Enter Last Name"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            InputProps={{ startAdornment: (<InputAdornment position="start"><BadgeOutlinedIcon /></InputAdornment>) }}
            required fullWidth sx={{ mb: 2 }}
          />
          <TextField
            placeholder="Enter Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            InputProps={{ startAdornment: (<InputAdornment position="start"><AccountCircleOutlinedIcon /></InputAdornment>) }}
            required fullWidth sx={{ mb: 2 }}
          />
          <TextField
            placeholder="Enter Email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            InputProps={{ startAdornment: (<InputAdornment position="start"><EmailOutlinedIcon /></InputAdornment>) }}
            required fullWidth sx={{ mb: 2 }}
          />
          <TextField
            placeholder="Enter Phone Number"
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            InputProps={{ startAdornment: (<InputAdornment position="start"><PhoneIcon /></InputAdornment>) }}
            required fullWidth sx={{ mb: 2 }}
          />
          <TextField
            placeholder="Enter Password"
            type={showPassword ? 'text' : 'password'}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            InputProps={{
              startAdornment: (<InputAdornment position="start"><LockOutlinedIcon /></InputAdornment>),
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={handleClickShowPassword} onMouseDown={handleMouseDownPassword} edge="end">
                    {showPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              )
            }}
            required fullWidth sx={{ mb: 2 }}
          />
          <TextField
            placeholder="Confirm Password"
            type={showConfirmPassword ? 'text' : 'password'}
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            InputProps={{
              startAdornment: (<InputAdornment position="start"><VpnKeyOutlinedIcon /></InputAdornment>),
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={handleClickShowConfirmPassword} onMouseDown={handleMouseDownPassword} edge="end">
                    {showConfirmPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              )
            }}
            required fullWidth sx={{ mb: 2 }}
          />
          <FormControlLabel
            control={
              <Checkbox
                checked={agreeTerms}
                onChange={(e) => setAgreeTerms(e.target.checked)}
              />
            }
            label="I agree to all terms*"
            sx={{ mb: 2, color: 'text.secondary' }}
            required
          />
          <Button type="submit" variant="contained" color="primary" size="large" fullWidth>Register</Button>
        </Box>
        <Typography sx={{ mt: 3, textAlign: 'center', color: 'text.secondary' }}>
          Already have an account? <MuiLink component={Link} to="/login" sx={{ fontWeight: 'bold' }}>Sign In</MuiLink>
        </Typography>
      </Box>
    </Paper>
  );
}

export default Register;
