import React from 'react';
import { Outlet, Link, useLocation, useNavigate } from 'react-router-dom';
import { Box, Typography, IconButton, Avatar } from '@mui/material';
import {
  Dashboard as DashboardIcon, Settings as SettingsIcon, LightbulbOutlined as LightbulbOutlinedIcon,
  TaskAlt as TaskAltIcon, Segment as SegmentIcon, HelpOutline as HelpOutlineIcon, ExitToApp as LogoutIcon
} from '@mui/icons-material';
import './Dashboard.css';

function DashboardLayout() {
  const location = useLocation();
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const sidebarItems = [
    { path: '/', icon: <DashboardIcon />, label: 'Dashboard' },
    { path: '/vital-task', icon: <LightbulbOutlinedIcon />, label: 'Vital Task' },
    { path: '/my-task', icon: <TaskAltIcon />, label: 'My Task' },
    { path: '/task-categories', icon: <SegmentIcon />, label: 'Task Categories' },
    { path: '/settings', icon: <SettingsIcon />, label: 'Settings' },
    { path: '/help', icon: <HelpOutlineIcon />, label: 'Help' }
  ];

  return (
    <Box className="dashboard-container">
      <Box className="sidebar">
        <Avatar className="user-avatar-v2">H</Avatar>
        <Typography variant="h6" className="user-name-v2">Harshith Kumar.M</Typography>
        <Typography variant="body2" className="user-email-v2">harshith.kumar@example.com</Typography>

        <Box className="sidebar-menu-v2">
          {sidebarItems.map((item) => (
            <Link to={item.path} key={item.label} className="menu-item-link">
              <Box className={`menu-item-v2 ${location.pathname === item.path ? 'active' : ''}`}>
                {item.icon} <Typography>{item.label}</Typography>
              </Box>
            </Link>
          ))}
        </Box>

        <Box className="logout-section-v2" onClick={handleLogout}>
          <LogoutIcon /> <Typography>Logout</Typography>
        </Box>
      </Box>

      <Box className="content-area-wrapper">
        {/* This Outlet renders the current page (Dashboard, MyTask, etc.) */}
        <Outlet />
      </Box>
    </Box>
  );
}

export default DashboardLayout;
