import React, { useState } from 'react';
import { Box, Typography, Button, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, IconButton } from '@mui/material';
import { Add as AddIcon, Edit as EditIcon, Delete as DeleteIcon } from '@mui/icons-material';
import CategoryEditModal from '../components/CategoryEditModal';

const initialStatuses = [
    { id: 1, name: 'Not Started' },
    { id: 2, name: 'In Progress' },
    { id: 3, name: 'Completed' },
];

const initialPriorities = [
    { id: 1, name: 'Low' },
    { id: 2, name: 'Moderate' },
    { id: 3, name: 'Extreme' },
];

function TaskCategories() {
    const [statuses, setStatuses] = useState(initialStatuses);
    const [priorities, setPriorities] = useState(initialPriorities);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentItem, setCurrentItem] = useState(null);
    const [categoryType, setCategoryType] = useState('');

    const handleOpenModal = (item, type) => {
        setCurrentItem(item);
        setCategoryType(type);
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setCurrentItem(null);
        setCategoryType('');
    };

    const handleSave = (item) => {
        const listMap = {
            'Status': { list: statuses, setList: setStatuses },
            'Priority': { list: priorities, setList: setPriorities }
        };
        const { list, setList } = listMap[categoryType];

        if (item.id) {
            setList(list.map(i => (i.id === item.id ? item : i)));
        } else {
            const newItem = { ...item, id: Date.now() };
            setList([...list, newItem]);
        }
        handleCloseModal();
    };

    const handleDelete = (id, type) => {
        if (window.confirm('Are you sure you want to delete this item?')) {
            if (type === 'Status') {
                setStatuses(statuses.filter(item => item.id !== id));
            } else if (type === 'Priority') {
                setPriorities(priorities.filter(item => item.id !== id));
            }
        }
    };

    const renderTable = (title, data, type) => (
        <Box component={Paper} sx={{ mb: 5, p: 3, borderRadius: '16px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)' }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2, pb: 2, borderBottom: 1, borderColor: 'divider' }}>
                <Typography variant="h5" sx={{ fontWeight: 'bold' }}>{title}</Typography>
                <Button variant="contained" startIcon={<AddIcon />} onClick={() => handleOpenModal(null, type)}>
                    Add New {type}
                </Button>
            </Box>
            <TableContainer>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>ID</TableCell>
                            <TableCell>Name</TableCell>
                            <TableCell align="right">Actions</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {data.map((item) => (
                            <TableRow key={item.id} sx={{ '&:last-child td, &:last-child th': { border: 0 } }}>
                                <TableCell>{item.id}</TableCell>
                                <TableCell>{item.name}</TableCell>
                                <TableCell align="right">
                                    <Button variant="outlined" startIcon={<EditIcon />} onClick={() => handleOpenModal(item, type)}>Edit</Button>
                                    <Button variant="outlined" color="error" startIcon={<DeleteIcon />} sx={{ ml: 1 }} onClick={() => handleDelete(item.id, type)}>Delete</Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </Box>
    );

    return (
        <Box sx={{ p: 4, bgcolor: '#f7f9fc', minHeight: '100vh' }}>
            <Typography variant="h4" sx={{ mb: 4, fontWeight: 'bold' }}>Manage Categories</Typography>
            <Button variant="contained" sx={{ mb: 4 }}>Add Category</Button>

            {renderTable('Task Status', statuses, 'Status')}
            {renderTable('Priorities', priorities, 'Priority')}

            <CategoryEditModal
                open={isModalOpen}
                onClose={handleCloseModal}
                onSave={handleSave}
                itemToEdit={currentItem}
                categoryType={categoryType}
            />
        </Box>
    );
}

export default TaskCategories;
