//
// FILE: src/lightTheme.js (THE FINAL, DEFINITIVE VERSION)
//
import { createTheme } from '@mui/material/styles';

export const lightTheme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#f05454', // The reddish-pink from your button
    },
    secondary: {
      main: '#007bff', // The blue for links
    },
    background: {
      default: '#fbe9e7',
      paper: '#ffffff',
    },
    text: {
      primary: '#121212',
      secondary: '#555555',
    },
  },
  typography: {
    fontFamily: '"Poppins", -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
    h4: { fontWeight: 600, },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: { borderRadius: 8, textTransform: 'none', padding: '12px 24px', fontWeight: 500, boxShadow: 'none' },
      },
    },
    MuiOutlinedInput: {
      styleOverrides: {
        root: { borderRadius: 12, },
        input: {
          '&:-webkit-autofill': {
            // --- THESE ARE THE CORRECTED LINES ---
            // '-webkit-box-shadow' has been changed to its camelCase version: WebkitBoxShadow
            WebkitBoxShadow: '0 0 0 100px #ffffff inset',
            // '-webkit-text-fill-color' has been changed to its camelCase version: WebkitTextFillColor
            WebkitTextFillColor: '#121212',
            borderRadius: 'inherit',
          },
        },
      },
    }
  },
});
