import React, { useState } from 'react';
import {
    Modal, Box, Typography, TextField, IconButton, Avatar, Divider, Paper
} from '@mui/material';
import { Close as CloseIcon, Send as SendIcon, SupportAgent as SupportAgentIcon } from '@mui/icons-material';
import { toast } from 'react-toastify';

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    borderRadius: '16px',
    boxShadow: 24,
    display: 'flex',
    flexDirection: 'column',
    height: '70vh',
    maxHeight: '550px'
};

function LiveChatModal({ open, onClose }) {
    const [message, setMessage] = useState('');

    const handleSendMessage = () => {
        if (message.trim()) {
            toast.success("Your message has been sent. An agent will be with you shortly.");
            setMessage('');
            onClose(); // Close the modal after sending
        }
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter' && !event.shiftKey) {
            event.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <Modal open={open} onClose={onClose}>
            <Box sx={style}>
                <Box sx={{ p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center', bgcolor: '#f5f5f5', borderTopLeftRadius: '16px', borderTopRightRadius: '16px' }}>
                    <Typography variant="h6" component="h2" sx={{ fontWeight: 'bold' }}>
                        Live Chat Support
                    </Typography>
                    <IconButton onClick={onClose}>
                        <CloseIcon />
                    </IconButton>
                </Box>
                <Box sx={{ flexGrow: 1, p: 2, overflowY: 'auto' }}>
                    <Box sx={{ display: 'flex', alignItems: 'flex-start', mb: 2 }}>
                        <Avatar sx={{ bgcolor: 'primary.main', mr: 1.5 }}>
                            <SupportAgentIcon />
                        </Avatar>
                        <Box>
                            <Typography variant="body2" sx={{ fontWeight: 'bold' }}>Support Bot</Typography>
                            <Paper elevation={0} sx={{ p: 1.5, bgcolor: '#f0f0f0', borderRadius: '12px' }}>
                                <Typography variant="body2">
                                    Welcome to Live Chat! Please describe your issue below, and a support agent will be with you shortly.
                                </Typography>
                            </Paper>
                        </Box>
                    </Box>
                </Box>
                <Divider />
                <Box sx={{ p: 2, bgcolor: '#f5f5f5', borderBottomLeftRadius: '16px', borderBottomRightRadius: '16px' }}>
                    <TextField
                        fullWidth
                        multiline
                        maxRows={3}
                        variant="outlined"
                        placeholder="Type your message..."
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        onKeyPress={handleKeyPress}
                        InputProps={{
                            endAdornment: (
                                <IconButton color="primary" onClick={handleSendMessage} disabled={!message.trim()}>
                                    <SendIcon />
                                </IconButton>
                            ),
                            sx: { borderRadius: '12px', bgcolor: 'white' }
                        }}
                    />
                </Box>
            </Box>
        </Modal>
    );
}

export default LiveChatModal;
