//
// FILE: src/components/ForgotPassword.js (THE FINAL, CENTERED VERSION)
//
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Box, TextField, Button, Typography, Paper, Link as MuiLink } from '@mui/material';

function ForgotPassword() {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');
    try {
      const response = await fetch('http://localhost:3001/forgot-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.message || 'Failed to send reset link');
      setMessage(data.message);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    // This Box provides the full-screen background and positioning
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-start',
        paddingTop: '15vh',
        backgroundColor: '#f9fafb',
        boxSizing: 'border-box'
      }}
    >
      {/* This Paper is the small, centered box */}
      <Paper component="div" elevation={3} sx={{ p: 4, borderRadius: 3, maxWidth: '400px', width: '100%' }}>
        <Box sx={{ textAlign: 'center', mb: 3 }}>
          <Typography variant="h4" component="h1" sx={{ fontWeight: 'bold' }}>Forgot Password</Typography>
          <Typography sx={{ color: 'text.secondary', mt: 1 }}>No problem! Enter your email below.</Typography>
        </Box>
        <Box component="form" onSubmit={handleSubmit}>
          <Typography variant="subtitle2" sx={{ fontWeight: 600, mb: 1 }}>Email</Typography>
          <TextField
            type="email" variant="outlined" value={email}
            onChange={(e) => setEmail(e.target.value)} required fullWidth sx={{ mb: 3 }}
          />
          <Button type="submit" variant="contained" color="primary" size="large" fullWidth>
            Send Reset Link
          </Button>
        </Box>
        {message && <p className="message">{message}</p>}
        {error && <p className="error">{error}</p>}
        <Typography sx={{ mt: 3, textAlign: 'center' }}>
          <MuiLink component={Link} to="/login" sx={{ fontWeight: 'bold' }}>Back to Login</MuiLink>
        </Typography>
      </Paper>
    </Box>
  );
}
export default ForgotPassword;
