import React, { useState } from 'react';
import { Box, Typography, IconButton, Card, CardContent } from '@mui/material';
import { Delete as DeleteIcon, Edit as EditIcon, RadioButtonUnchecked, RadioButtonChecked } from '@mui/icons-material';
import EditVitalTaskModal from './EditVitalTaskModal'; // Ensure this component exists

// Sample data matching the design
const initialVitalTasks = [
  { 
    id: 1, 
    title: "Walk the dog", 
    description: "Take the dog to the park and bring treats as well....", 
    fullDescription: "Take Luffy and Jiro for a leisurely stroll around the neighborhood. Enjoy the fresh air and give them the exercise and mental stimulation they need for a happy and healthy day.\n\nKey Points:\n1. Listen to a podcast or audiobook\n2. Practice mindfulness or meditation\n3. Take photos of interesting sights\n4. Practice obedience training\n5. Chat with neighbors or other walkers",
    priority: "Extreme", 
    status: "Not Started", 
    createdOn: "20/06/2023", 
    image: "https://images.unsplash.com/photo-1556228720-195a672e8a03?fit=crop&w=600&h=400"
  },
  { 
    id: 2, 
    title: "Go back home and take grandma to the hospital", 
    description: "Appointment with Dr. Smith at 2 PM.", 
    fullDescription: "Ensure grandma has her medical files and any required medication. The appointment is at the City General Hospital, Cardiology department. Plan to leave by 1 PM to account for traffic.",
    priority: "Moderate", 
    status: "In Progress", 
    createdOn: "20/06/2023", 
    image: "https://images.unsplash.com/photo-1584982751601-97dcc096659c?fit=crop&w=600&h=400" 
  },
];

// Helper to get priority color
const getPriorityColor = (priority) => {
    if (priority === 'Extreme') return '#ff5757';
    if (priority === 'Moderate') return '#3ABEFE';
    return '#4caf50'; // Low
};

function VitalTask() {
  const [tasks, setTasks] = useState(initialVitalTasks);
  const [selectedTask, setSelectedTask] = useState(tasks.length > 0 ? tasks[0] : null);
  const [isEditModalOpen, setEditModalOpen] = useState(false);

  const handleDeleteTask = (taskId) => {
    if (window.confirm("Are you sure you want to delete this vital task?")) {
      const newTasks = tasks.filter(task => task.id !== taskId);
      setTasks(newTasks);
      if (selectedTask && selectedTask.id === taskId) {
        setSelectedTask(newTasks.length > 0 ? newTasks[0] : null);
      }
    }
  };

  const handleUpdateTask = (updatedTask) => {
    const newTasks = tasks.map(task => (task.id === updatedTask.id ? updatedTask : task));
    setTasks(newTasks);
    setSelectedTask(updatedTask);
    setEditModalOpen(false);
  };

  return (
    <>
      <EditVitalTaskModal 
        open={isEditModalOpen} 
        onClose={() => setEditModalOpen(false)} 
        taskToEdit={selectedTask}
        onUpdateTask={handleUpdateTask}
      />
      {/* Main container with scoped styles */}
      <Box sx={{ display: 'flex', height: '100%', gap: 3, p: 0 }}>

        {/* Left Column: Task List */}
        <Box sx={{ width: '450px', flexShrink: 0, display: 'flex', flexDirection: 'column', gap: 3, overflowY: 'auto', pr: 2 }}>
          {tasks.map(task => (
            <Card 
              key={task.id}
              onClick={() => setSelectedTask(task)}
              sx={{
                bgcolor: '#2c2c2e', color: '#fff', borderRadius: '20px', cursor: 'pointer', p: 1,
                border: '2px solid', borderColor: selectedTask?.id === task.id ? '#ff6f61' : 'transparent',
                boxShadow: selectedTask?.id === task.id ? '0 0 15px rgba(255, 111, 97, 0.4)' : 'none',
                transition: 'all 0.3s ease', '&:hover': { transform: 'translateY(-4px)' }
              }}
            >
              <CardContent sx={{ p: '16px !important', display: 'grid', gridTemplateColumns: '1fr 100px', gap: '12px 16px' }}>
                <Box sx={{ gridColumn: '1 / 2', display: 'flex', alignItems: 'center', gap: 1.5 }}>
                  {task.status === 'In Progress' ? <RadioButtonChecked sx={{ color: getPriorityColor(task.priority) }} /> : <RadioButtonUnchecked sx={{ color: '#888' }} />}
                  <Typography variant="h6" sx={{ fontSize: '1.1rem', fontWeight: 600 }}>{task.title}</Typography>
                </Box>
                <Typography variant="body2" sx={{ gridColumn: '1 / 2', color: '#b0b0b0', fontSize: '0.9rem' }}>{task.description}</Typography>
                <Box sx={{ gridColumn: '2 / 3', gridRow: '1 / 3', width: '100px', height: '80px', borderRadius: '12px', overflow: 'hidden' }}>
                    <img src={task.image} alt={task.title} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                </Box>
                <Box sx={{ gridColumn: '1 / 3', display: 'flex', justifyContent: 'space-between', fontSize: '0.8rem', color: '#b0b0b0', borderTop: '1px solid #444', pt: 1.5, mt: 1 }}>
                  <Typography variant="caption">Priority: <span style={{ color: getPriorityColor(task.priority), fontWeight: 'bold' }}>{task.priority}</span></Typography>
                  <Typography variant="caption">Status: <span style={{ color: getPriorityColor(task.priority), fontWeight: 'bold' }}>{task.status}</span></Typography>
                </Box>
              </CardContent>
            </Card>
          ))}
        </Box>

        {/* Right Column: Task Details */}
        <Box sx={{ flex: 1, bgcolor: '#111', borderRadius: '20px', p: 4, position: 'relative', overflowY: 'auto' }}>
          {selectedTask ? (
            <>
              <Box sx={{ width: '100%', height: '300px', borderRadius: '16px', mb: 3, overflow: 'hidden' }}>
                <img src={selectedTask.image} alt={selectedTask.title} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
              </Box>
              <Box sx={{ display: 'flex', gap: 3, color: '#b0b0b0', mb: 2 }}>
                <Typography variant="body2">Priority: <span style={{ color: getPriorityColor(selectedTask.priority), fontWeight: '600' }}>{selectedTask.priority}</span></Typography>
                <Typography variant="body2">Status: <span style={{ color: getPriorityColor(selectedTask.priority), fontWeight: '600' }}>{selectedTask.status}</span></Typography>
                <Typography variant="body2">Created on: {selectedTask.createdOn}</Typography>
              </Box>
              <Typography variant="h4" sx={{ mb: 3, fontWeight: '700', color: '#fff' }}>{selectedTask.title}</Typography>
              <Typography variant="body1" sx={{ lineHeight: 1.7, color: '#e0e0e0', whiteSpace: 'pre-wrap' }}>{selectedTask.fullDescription}</Typography>
              <Box sx={{ position: 'absolute', bottom: 32, right: 32, display: 'flex', gap: 2 }}>
                <IconButton onClick={() => handleDeleteTask(selectedTask.id)} sx={{ width: 60, height: 60, color: 'white', bgcolor: '#ff6f61', '&:hover': { bgcolor: '#e05252', transform: 'translateY(-3px)' } }}><DeleteIcon /></IconButton>
                <IconButton onClick={() => setEditModalOpen(true)} sx={{ width: 60, height: 60, color: 'white', bgcolor: '#3ABEFE', '&:hover': { bgcolor: '#30a0d9', transform: 'translateY(-3px)' } }}><EditIcon /></IconButton>
              </Box>
            </>
          ) : (
            <Typography sx={{ color: 'white', textAlign: 'center', mt: 5 }}>No vital tasks selected.</Typography>
          )}
        </Box>
      </Box>
    </>
  );
}

export default VitalTask;
