import React, { useState } from 'react';
import { Outlet, useOutletContext, NavLink } from 'react-router-dom';
import { Box, Typography, TextField, Avatar, List, ListItem, ListItemButton, ListItemIcon, ListItemText, IconButton, Menu, MenuItem } from '@mui/material';
import {
    Dashboard as DashboardIcon,
    Favorite as VitalTaskIcon,
    CheckCircle as MyTaskIcon,
    Category as TaskCategoriesIcon,
    Settings as SettingsIcon,
    Help as HelpIcon,
    Logout as LogoutIcon,
    Search as SearchIcon,
    Notifications as NotificationsIcon,
    CalendarToday as CalendarIcon
} from '@mui/icons-material';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider, DatePicker } from '@mui/x-date-pickers';
import ProfileModal from './ProfileModal';

const Header = ({ searchTerm, onSearchChange, selectedDate, onDateChange, onNotificationClick }) => (
    <Box sx={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        p: 2,
        bgcolor: '#f4f6f8',
        borderBottom: '1px solid #e0e0e0',
    }}>
        <Box sx={{ flex: 1, display: 'flex', justifyContent: 'flex-start' }}>
            <Typography variant="h4" sx={{ fontWeight: 'bold' }}>Dashboard</Typography>
        </Box>
        <Box sx={{ flex: 1, display: 'flex', justifyContent: 'center' }}>
            <TextField
                variant="outlined"
                placeholder="Search your task here..."
                value={searchTerm}
                onChange={onSearchChange}
                InputProps={{
                    startAdornment: <SearchIcon sx={{ color: 'text.disabled', mr: 1 }} />,
                    sx: { borderRadius: '12px', bgcolor: 'white' }
                }}
                sx={{ width: '100%', maxWidth: '450px' }}
            />
        </Box>
        <Box sx={{ flex: 1, display: 'flex', justifyContent: 'flex-end', alignItems: 'center', gap: 2 }}>
            <IconButton onClick={onNotificationClick}>
                <NotificationsIcon />
            </IconButton>
            <DatePicker
                value={selectedDate}
                onChange={onDateChange}
                slots={{
                    textField: (params) => <TextField {...params} sx={{ width: '160px' }} />,
                    openPickerIcon: CalendarIcon,
                }}
                slotProps={{
                    textField: {
                        variant: 'outlined',
                        InputProps: { sx: { borderRadius: '12px', bgcolor: 'white' } }
                    }
                }}
            />
        </Box>
    </Box>
);

const Sidebar = ({ user, onProfileClick }) => {
    const navItems = [
        { text: 'Dashboard', icon: <DashboardIcon />, path: '/' },
        { text: 'Vital Task', icon: <VitalTaskIcon />, path: '/vital-task' },
        { text: 'My Task', icon: <MyTaskIcon />, path: '/my-task' },
        { text: 'Task Categories', icon: <TaskCategoriesIcon />, path: '/task-categories' },
        { text: 'Settings', icon: <SettingsIcon />, path: '/settings' }
    ];

    return (
        <Box sx={{
            width: 280,
            flexShrink: 0,
            bgcolor: '#D32F2F',
            height: '100vh',
            display: 'flex',
            flexDirection: 'column',
            color: 'white',
        }}>
            <Box sx={{ p: 2, textAlign: 'center', mt: 2 }}>
                <Avatar
                    alt={user.name}
                    src={user.avatar}
                    sx={{ width: 80, height: 80, mx: 'auto', mb: 1, cursor: 'pointer', border: '2px solid white' }}
                    onClick={onProfileClick}
                />
                <Typography variant="h6" onClick={onProfileClick} sx={{ cursor: 'pointer' }}>{user.name}</Typography>
                <Typography variant="body2" onClick={onProfileClick} sx={{ cursor: 'pointer', opacity: 0.8 }}>{user.email}</Typography>
            </Box>
            <List sx={{ p: 1 }}>
                {navItems.map((item) => (
                    <ListItem key={item.text} disablePadding>
                        <ListItemButton
                            component={NavLink}
                            to={item.path}
                            sx={{
                                borderRadius: '8px',
                                mb: 1,
                                '&.active': { bgcolor: 'rgba(255, 255, 255, 0.2)' },
                                '&:hover': { bgcolor: 'rgba(255, 255, 255, 0.1)' }
                            }}
                        >
                            <ListItemIcon sx={{ color: 'inherit' }}>{item.icon}</ListItemIcon>
                            <ListItemText primary={item.text} />
                        </ListItemButton>
                    </ListItem>
                ))}
            </List>
            <Box sx={{ flexGrow: 1 }} />
            <List sx={{ p: 1 }}>
                <ListItem disablePadding>
                    <ListItemButton sx={{ borderRadius: '8px' }}>
                        <ListItemIcon sx={{ color: 'inherit' }}><HelpIcon /></ListItemIcon>
                        <ListItemText primary="Help" />
                    </ListItemButton>
                </ListItem>
                <ListItem disablePadding>
                    <ListItemButton sx={{ borderRadius: '8px' }}>
                        <ListItemIcon sx={{ color: 'inherit' }}><LogoutIcon /></ListItemIcon>
                        <ListItemText primary="Logout" />
                    </ListItemButton>
                </ListItem>
            </List>
        </Box>
    );
};

function DashboardLayout() {
    const [isProfileModalOpen, setProfileModalOpen] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [anchorEl, setAnchorEl] = useState(null);
    const [currentUser, setCurrentUser] = useState({
        name: 'Harshith Kumar.M',
        email: 'mharshithkumar6@gmail.com',
        role: 'Full Stack Developer',
        phone: '8977795636',
        location: 'Dehradun, India',
        avatar: ''
    });

    const handleUpdateUser = (updatedData) => {
        setCurrentUser(updatedData);
        setProfileModalOpen(false);
    };

    const handleNotificationClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleNotificationClose = () => {
        setAnchorEl(null);
    };

    const isNotificationMenuOpen = Boolean(anchorEl);

    return (
        <LocalizationProvider dateAdapter={AdapterDateFns}>
            <Box sx={{ display: 'flex' }}>
                <Sidebar
                    user={currentUser}
                    onProfileClick={() => setProfileModalOpen(true)}
                />
                <Box sx={{ display: 'flex', flexDirection: 'column', flexGrow: 1, height: '100vh' }}>
                    <Header
                        searchTerm={searchTerm}
                        onSearchChange={(e) => setSearchTerm(e.target.value)}
                        selectedDate={selectedDate}
                        onDateChange={(newDate) => setSelectedDate(newDate)}
                        onNotificationClick={handleNotificationClick}
                    />
                    <Box component="main" sx={{ flexGrow: 1, p: 3, bgcolor: '#f4f6f8', overflow: 'auto' }}>
                        <Outlet context={{ searchTerm, setSearchTerm, selectedDate }} />
                    </Box>
                </Box>
                <ProfileModal
                    open={isProfileModalOpen}
                    onClose={() => setProfileModalOpen(false)}
                    user={currentUser}
                    onSave={handleUpdateUser}
                />
                <Menu
                    anchorEl={anchorEl}
                    open={isNotificationMenuOpen}
                    onClose={handleNotificationClose}
                >
                    <MenuItem onClick={handleNotificationClose}>Notification 1: New task assigned</MenuItem>
                    <MenuItem onClick={handleNotificationClose}>Notification 2: Project deadline approaching</MenuItem>
                    <MenuItem onClick={handleNotificationClose}>Notification 3: Team member mentioned you</MenuItem>
                </Menu>
            </Box>
        </LocalizationProvider>
    );
}

export function useDashboardContext() {
    return useOutletContext();
}

export default DashboardLayout;
